mapper.select:
    SELECT 
	  `record_id`,
	  `consumer_id`,
	  `consumer_name`,
	  `shop_id`,
	  `shop_name`,
	  `product_id`,
	  `product_name`,
	  `points`,
	  `create_time`,
	  `consumer_visible`,
	  `shopkeeper_visible`,
	  `valid` 
	FROM
	  `tb_points_record`


           `consumer_id` = #{condition.consumerId}


           AND `shop_id` = #{condition.shopId}


	       AND `product_id` = #{condition.productId}


           AND `consumer_visible` = #{condition.consumerVisible}


           AND `shopkeeper_visible` = #{condition.shopkeeperVisible}


           AND `valid` = #{condition.valid}


           AND `oper_type` = #{condition.operType}


           AND (`consumer_name` LIKE CONCAT('%', #{searchKey}, '%') OR `shop_name` LIKE CONCAT('%', #{searchKey}, '%') OR `product_name` LIKE CONCAT('%', #{searchKey}, '%'))



        ORDER BY ${pagingParams.orderRules}


        LIMIT #{pagingParams.rowIndex}, #{pagingParams.rowSize}

SELECT 
  COUNT(1)
FROM
  `tb_points_record`


       `consumer_id` = #{condition.consumerId}


       AND `shop_id` = #{condition.shopId}


       AND `product_id` = #{condition.productId}


       AND `consumer_visible` = #{condition.consumerVisible}


       AND `shopkeeper_visible` = #{condition.shopkeeperVisible}


       AND `valid` = #{condition.valid}


       AND `oper_type` = #{condition.operType}


       AND (`consumer_name` LIKE CONCAT('%', #{searchKey}, '%') OR `shop_name` LIKE CONCAT('%', #{searchKey}, '%') OR `product_name` LIKE CONCAT('%', #{searchKey}, '%'))

SELECT pr.`consumer_id`, u.`name` AS `consumer_name`, SUM(IF(pr.`oper_type` = 1, pr.`points`, -pr.`points`)) AS `total_points` FROM `tb_points_record` pr JOIN `tb_user_info` u ON pr.`consumer_id` = u.`user_id` WHERE pr.`valid` = 1 AND pr.`shop_id` = #{shopId}
AND u.`name` LIKE CONCAT('%', #{searchKey}, '%')
GROUP BY pr.`consumer_id`, u.`name` ORDER BY `total_points` DESC
LIMIT #{pagingParams.rowIndex}, #{pagingParams.rowSize}

SELECT pr.`shop_id`, s.`shop_name`, MAX(pr.`create_time`) AS `last_update_time`, SUM(pr.`oper_type` * pr.`points`) AS `total_points` FROM `tb_points_record` pr JOIN `tb_shop` s ON pr.`shop_id` = s.`shop_id` WHERE pr.`valid` = 1 AND pr.`consumer_id` = #{consumerId}
AND s.`shop_name` LIKE CONCAT('%', #{searchKey}, '%')
GROUP BY pr.`shop_id`, s.`shop_name` ORDER BY `total_points` DESC
LIMIT #{pagingParams.rowIndex}, #{pagingParams.rowSize}

SELECT `record_id`, `product_id`, `product_name`, `img_addr`, `points`, `oper_type`, `create_time` FROM (SELECT `points_in`.`record_id`, `points_in`.`product_id`, `points_in`.`points`, `points_in`.`oper_type`, `points_in`.`create_time`, `prod`.`product_name`, `prod`.`img_addr` FROM (SELECT `record_id`, `product_id`, `points`, `oper_type`, `create_time` FROM `tb_points_record` WHERE `consumer_id` = #{consumerId} AND `shop_id` = #{shopId}
AND `create_time` >= STR_TO_DATE(#{beginDate}, '%Y-%m-%d')
AND `create_time` < DATE_ADD( STR_TO_DATE(#{endDate}, '%Y-%m-%d'), INTERVAL 1 DAY )
AND true = #{isPointsInShow} AND `oper_type` = 1 AND `consumer_visible` = 1 AND `valid` = 1) AS `points_in` LEFT JOIN `tb_product` AS `prod` ON `points_in`.`product_id` = `prod`.`product_id`
`prod`.`product_name` LIKE CONCAT('%', #{searchKey}, '%')
UNION ALL SELECT `points_out`.`record_id`, `points_out`.`product_id`, `points_out`.`points`, `points_out`.`oper_type`, `points_out`.`create_time`, `award`.`award_name` AS `product_name`, `award`.`award_img` AS `img_addr` FROM (SELECT `record_id`, `product_id`, `points`, `oper_type`, `create_time` FROM `tb_points_record` WHERE `consumer_id` = #{consumerId} AND `shop_id` = #{shopId}
AND `create_time` >= STR_TO_DATE(#{beginDate}, '%Y-%m-%d')
AND `create_time` < DATE_ADD( STR_TO_DATE(#{endDate}, '%Y-%m-%d'), INTERVAL 1 DAY )
AND true = #{isPointsOutShow} AND `oper_type` = - 1 AND `consumer_visible` = 1 AND `valid` = 1) AS `points_out` LEFT JOIN `tb_award` AS `award` ON `points_out`.`product_id` = `award`.`award_id`
`award`.`award_name` LIKE CONCAT('%', #{searchKey}, '%')
) r ORDER BY `create_time` DESC
LIMIT #{pagingParams.rowIndex}, #{pagingParams.rowSize}
